uint8_t
PollRxBuff(void) {
    uint8_t byRxData;
    uint8_t byUartState = (uint8_t)UART_STATE_IDLE;

    while ((bufNumItems(&serialQueueRx) != 0) && (byUartState == UART_STATE_IDLE))
    {
        bufDeDat(&serialQueueRx, &byRxData);

        switch (byRxBufState) {
            case RX_STATE_START_BYTE:
                if (byRxData == FRAME_SOF) {
                    byIndexRxBuf = 0;
                    bRxActive = 1;
                    byCheckXorRxBuf = CXOR_INIT_VAL;
                    byRxBufState = RX_STATE_DATA_BYTES;
                    TimerGetCurrentTime(&dwTimeoutRx);
                }
                else if (byRxData == FRAME_ACK) {
                    byUartState = UART_STATE_ACK_RECEIVED;
                }
                else if (byRxData == FRAME_NACK) {
                    byUartState = UART_STATE_NACK_RECEIVED;
                }
                else {
                    bRxActive = 0;
                    byUartState= UART_STATE_ERROR;
                }
                break;

            case RX_STATE_DATA_BYTES:
                if (byIndexRxBuf < RX_BUFFER_SIZE) {
                    byRxBuffer[byIndexRxBuf] = byRxData;
                    if (byIndexRxBuf > 0) {
                        /* Calculate check xor */
                        byCheckXorRxBuf ^= byRxData;
                    }
                    if (++byIndexRxBuf == FlenBuf) {
                        byRxBufState = RX_STATE_CXOR_BYTE;
                    }
                }
                else {
                    byRxBufState = RX_STATE_START_BYTE;
                    byUartState = UART_STATE_ERROR;
                }
                TimerGetCurrentTime(&dwTimeoutRx);
                break;

            case RX_STATE_CXOR_BYTE:
                if (byRxData == byCheckXorRxBuf) {
                    byUartState = UART_STATE_DATA_RECEIVED;
                }
                else {
                    byUartState = UART_STATE_ERROR;
                }
                bRxActive = 0;

            default:
                byRxBufState = RX_STATE_START_BYTE;
                break;
        }
    }

    /* Check timeout rx */
    if (byUartState == UART_STATE_IDLE) {
        if (bRxActive && (TimerGetElapsedTime(dwTimeoutRx) >= RX_TIMEOUT)) {
            bRxActive = 0;
            byRxBufState = RX_STATE_START_BYTE;
            byUartState  = UART_STATE_RX_TIMEOUT;
        }
    }

    return byUartState;
}

//HAM MAIN
int main(void)
{
	AppInitCommon();
	SetStateApp(STATE_APP_STARTUP);
	EventSchedulerAdd(EVENT_APP_INIT);
    /* Loop forever */
	while(1)
	{
		processTimerScheduler();
		processEventScheduler();
		processSerialReceiver();
	}
}

//Goi ham co san
void AppInitCommon(void)
{
	SystemCoreClockUpdate();
	TimerInit();
	EventSerial_Init();
	EventSerial_SetEventLedCallback(LedCmdSetState);
	EventSerial_SetEventButtonCallback(ButtonCmdSetState);
	EventSerial_SetEventBuzzerCallback(BuzzerCmdSetState);
	EventSerial_SetEventLcdCallback(LcdCmdSetState);
	EventButton_Init();
	EventSchedulerInit(AppStateManager);
	BuzzerControl_Init();
	LedControl_Init();
	LightSensor_Init(ADC_READ_MODE_DMA);
	TemHumSensor_Init();

	//In ra LCD
	Ucglib4WireSWSPI_begin(&ucg, UCG_FONT_MODE_SOLID);
	ucg_ClearScreen(&ucg);
	ucg_SetFont(&ucg, ucg_font_ncenR12_hr);
	ucg_SetColor(&ucg, 0, 255, 255, 255);
	ucg_SetColor(&ucg, 1, 0, 0, 0);
	ucg_SetRotate180(&ucg);
	LoadConfiguration();

}

//Goi ham su kien
static void AppStateManager(uint8_t event)
{
	switch(GetStateApp())
	{
		case STATE_APP_STARTUP:
			if(event == EVENT_APP_INIT)
			{
				//Load configuration
				LoadConfiguration();
				SetStateApp(STATE_APP_IDLE);
			}
			break;
		case STATE_APP_IDLE:
			{
				DeviceStateMachine(event);
			}
			break;
		case STATE_APP_RESET:
			break;
		default:
			break;
	}
}


static void SetStateApp(state_app_t state)
{
	// Set state of application
	eCurrentState = state;
}

static state_app_t GetStateApp()
{
	//Return state of application
	return eCurrentState;
}

void LedCmdSetState(uint8_t led_id, \
		 uint8_t led_color, \
		 uint8_t led_num_blink, \
		 uint8_t led_interval, \
		 uint8_t led_last_state)
{
		LedControl_SetColorGeneral(led_id, led_color, 00);
		if(led_last_state == 4)
		{
			LedControl_SetColorGeneral(led_id, led_color, 0);
		}
		else if(led_last_state != 4)
		{
			LedControl_SetColorGeneral(led_id, led_color, 50);
		}
}
